import React, { useState } from "react";
import Player from "./player";
import { PlayerData } from "./playerData";
import DiceRoll from "./diceRoll";
import "../styles/game.css";

type GameProps = {
  numberOfPlayers: number;
};

function Game({ numberOfPlayers }: GameProps) {
  const [currentPlayer, setCurrentPlayer] = useState(1);
  //   const [players, setCurrentPlayer] = useState<PlayerData[]>([
  //     { id: 1, globalScore: 0, roundScore: 0 },
  //     { id: 2, globalScore: 0, roundScore: 0 },
  //   ]);
  const [diceRollsLeft, setDiceRollsLeft] = useState(2);

  const rollDice = () => {
    // TODO: Gérer le lancer de dé
    setDiceRollsLeft(diceRollsLeft - 1);

    if (diceRollsLeft === 1) {
      setCurrentPlayer((currentPlayer % numberOfPlayers) + 1);
      setDiceRollsLeft(2);
    }
  };

  return (
    <>
      <div className="image">
        <div className="game-container">
          <h1>Mon jeu de dés</h1>
          <div className="player-info-container"></div>
          <div className="game__turn">
            <h2>Tour de {setCurrentPlayer.arguments}</h2>
            <div className="player-name">
              <p>Roll</p>

              <DiceRoll onRoll={rollDice} />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Game;
