import React, { useState } from "react";
import {
  FaDiceOne,
  FaDiceTwo,
  FaDiceThree,
  FaDiceFour,
  FaDiceFive,
  FaDiceSix,
} from "react-icons/fa";

type DiceRollProps = {
  onRoll: (value: number) => void;
};

const DiceRoll = ({ onRoll }: DiceRollProps) => {
  const [diceValue, setDiceValue] = useState<number>(1);

  const rollDice = () => {
    const value = Math.floor(Math.random() * 6) + 1;
    setDiceValue(value);
    onRoll(value);
  };

  const renderDice = (value: number) => {
    switch (value) {
      case 1:
        return <FaDiceOne />;
      case 2:
        return <FaDiceTwo />;
      case 3:
        return <FaDiceThree />;
      case 4:
        return <FaDiceFour />;
      case 5:
        return <FaDiceFive />;
      case 6:
        return <FaDiceSix />;
      default:
        return null;
    }
  };

  return (
    <div className="dice-roll">
      <div className="dice-roll__container" onClick={rollDice}>
        {renderDice(diceValue)}
      </div>
      <div className="dice-roll__value">{diceValue}</div>
    </div>
  );
};

export default DiceRoll;
