import React, { useState } from "react";
import Player from "./player";
import { PlayerData } from "./playerData";
import DiceRoll from "./diceRoll";
import "../styles/game.css";

const buttonStyles = {
  backgroundColor: "black",
  color: "white",
  padding: "10px",
  margin: "0.5rem",
  borderRadius: "0.2rem",
  width: "100%",
  boxShadow: "none",
  border: "none",
  cursor: "pointer",
  fontSize: 16,
  fontWeight: "bold",
  letterSpacing: 0.25,
};
type RefreshIconProps = {
  size?: number;
  color?: string;
};

const RefreshIcon = ({ size = 24, color = "black" }: RefreshIconProps) => {
  return (
    <svg width={size} height={size} viewBox="0 0 24 24">
      <path
        fill={color}
        d="M16.59,7.58A8,8,0,1,0,20,12H18a6,6,0,1,1-3.91-5.39l-.5.86a5,5,0,1,0,8.11,5.39l2.24-2.24A1,1,0,0,0,23,8V3a1,1,0,0,0-1.71-.7l-2.24,2.24A7.93,7.93,0,0,0,16.59,7.58Z"
      />
    </svg>
  );
};

function Game() {
  const [players, setPlayers] = useState<PlayerData[]>([]);
  const [currentPlayerIndex] = useState<number>(0);

  //   const addPlayer = (name: string) => {
  //     const newPlayer: PlayerData = {
  //       id: players.length + 1,
  //       name,
  //       globalScore: 0,
  //       roundScore: 0,
  //     };
  //     setPlayers([...players, newPlayer]);
  //   };

  const rollDice = (value: number) => {
    // Mettre à jour le score du joueur en cours
    const updatedPlayers = players.map((player, index) => {
      if (index === currentPlayerIndex) {
        return {
          ...player,
          roundScore: player.roundScore + value,
        };
      }
      return player;
    });
    setPlayers(updatedPlayers);
  };

  return (
    <>
      <div className="image">
        <div className="game-container">
          <h1>Mon jeu de dés</h1>
          <div>
            {players.map((player) => (
              <Player key={player.id} player={player} id={2} isActive={false} />
            ))}
            <button
              style={buttonStyles}
              className="click"
              onClick={() => rollDice()}
            >
              <RefreshIcon size={16} color="white" />
            </button>
          </div>
          <div className="game__turn">
            {/* <h2>Tour de {players[currentPlayerIndex].id}</h2> */}
            <div className="player-name">
              <p>Roll</p>
              <DiceRoll onRoll={rollDice} />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Game;
