import React, { useState } from "react";
import Player from "./player";
import { PlayerData } from "./playerData";
import DiceRoll from "./diceRoll";
import "../styles/game.css";

const buttonStyles = {
  backgroundColor: "black",
  color: "white",
  padding: "10px",
  margin: "0.5rem",
  borderRadius: "0.2rem",
  width: "40%",
  boxShadow: "none",
  border: "none",
  cursor: "pointer",
  fontSize: 16,
  fontWeight: "bold",
  letterSpacing: 0.25,
};
function resetGame() {
  return [
    { id: 1, globalScore: 0, roundScore: 0 },
    { id: 2, globalScore: 0, roundScore: 0 },
  ];
}

function getWinner(players: PlayerData[]): number | undefined {
  // Trouver le joueur avec le score global le plus élevé
  const maxScore = Math.max(...players.map((player) => player.globalScore));
  const winner = players.find((player) => player.globalScore === maxScore);

  // S'il y a un gagnant, retourner son id
  if (winner) {
    return winner.id;
  }

  // S'il n'y a pas de gagnant, retourner undefined
  return undefined;
}

function Game() {
  function restartGame() {
    setPlayers([
      { id: 1, globalScore: 0, roundScore: 0 },
      { id: 2, globalScore: 0, roundScore: 0 },
    ]);
    setCurrentPlayerIndex(0);
  }
  const [players, setPlayers] = useState<PlayerData[]>([
    { id: 1, globalScore: 0, roundScore: 0 },
    { id: 2, globalScore: 0, roundScore: 0 },
  ]);
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState<number>(0);

  const rollDice = (value: number) => {
    // Mettre à jour le score du joueur en cours
    const updatedPlayers = players.map((player, index) => {
      if (index === currentPlayerIndex) {
        return {
          ...player,
          roundScore: player.roundScore + value,
        };
      }
      return player;
    });
    console.log(updatedPlayers);
    console.log(getWinner);
    const winnerId = getWinner(players);
    if (winnerId) {
      console.log(`Le joueur ${winnerId} a gagné !`);
    } else {
      console.log("Il n'y a pas de gagnant pour le moment.");
    }
    getWinner(updatedPlayers);
    // Si le joueur actuel a fait deux lancers, passer au joueur suivant
    if (value === 1 || value === 6) {
      const nextPlayerIndex = (currentPlayerIndex + 1) % players.length;
      setCurrentPlayerIndex(nextPlayerIndex);
    }

    // Mettre à jour les scores globaux si le joueur a atteint 100 points
    const currentPlayer = updatedPlayers[currentPlayerIndex];
    if (currentPlayer.roundScore + currentPlayer.globalScore >= 100) {
      alert(`Le joueur ${currentPlayer.id} a gagné !`);

      setPlayers(resetGame);
      setCurrentPlayerIndex(0);
    } else {
      setPlayers(updatedPlayers);
    }
  };

  return (
    <>
      <div className="image">
        <div className="game-container">
          <button style={buttonStyles} className="click" onClick={restartGame}>
            New Game
          </button>
          <div className="player-info-container">
            {players.map((player, index) => (
              <Player
                key={player.id}
                player={player}
                id={index + 1}
                isActive={index === currentPlayerIndex}
              />
            ))}
          </div>
          <div className="game__turn">
            <h2>Tour de Joueur {players[currentPlayerIndex].id}</h2>
            <div className="player-name">
              <DiceRoll onRoll={rollDice} />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Game;
