import { PlayerData } from "./playerData";
import "../styles/game.css";

type PlayerProps = {
  id: number;
  player: PlayerData;
  isActive: boolean;
};

export function getWinner(players: PlayerData[]): number | undefined {
  // Trouver le joueur avec le score global le plus élevé
  const maxScore = Math.max(...players.map((player) => player.globalScore));
  const winner = players.find((player) => player.globalScore === maxScore);

  // S'il y a un gagnant, retourner son id
  if (winner) {
    return winner.id;
  }

  // S'il n'y a pas de gagnant, retourner undefined
  return undefined;
}

const Player = ({ player, isActive }: PlayerProps) => {
  return (
    <div className={`player ${isActive ? "active" : ""}`}>
      <div className="player-name">Player {player.id}</div>

      <div>
        <getWinner />
      </div>
      <div className="score-container">CURRENT {player.roundScore}</div>
    </div>
  );
};

export default Player;
